	import { pageCategory, pageLink, saveLink } from "@/api/diy";
	import { treeListApi, changeListApi } from "@/api/product";
	import { seckillListApi, combinationListApi, bargainListApi, integralProductListApi } from "@/api/marketing";
	import { cmsListApi } from '@/api/cms';
    export default {
        name: 'linkaddress',
        data () {
            return {
                modals: false,
				categoryData:[],
				currenType:'link',
				columns: [
					{
						title: "ID",
						key: "id",
						width: 60,
					},
					{
						title: "页面名称",
						key: "name",
						width: 150,
					},
					{
						title: "页面链接",
						key: "url"
					}
				],
				columns7:[
					{
						title: "ID",
						key: "id",
						width: 60,
					},
					{
						title: "分类名称",
						key: "cate_name",
						tree: true
					},
					{
						title: "分类图标",
						slot: "pic"
					}
				],
				columns8:[
					{
						title: "ID",
						key: "id",
						width: 60
					},
					{
						title: "商品图片",
						slot: "image",
						width: 90,
					},
					{
						title: "商品名称",
						key: "store_name"
					}
				],
				bargain:[
					{
						title: "ID",
						key: "id",
						width: 60
					},
					{
						title: "商品图片",
						slot: "image",
						width: 90,
					},
					{
						title: "商品名称",
						key: "title"
					}
				],
				news:[
					{
						title: "ID",
						key: "id",
						width: 60
					},
					{
						title: "文章图片",
						slot: "image_input",
						width: 90,
					},
					{
						title: "文章名称",
						key: "title"
					}
				],
				formValidate: {
					page: 1,
					limit: 15,
					cate_id: "",
					store_name: ""
				},
				total: 0,
				basicsList:[],
				userList:[],
				distributionList:[],
				coupon:[],
				luckDraw: [],
				integral: [],
                presale: [],
				currenId:'',
				currenUrl:'',
				loading:false,
				tableList:[],
				presentId:0,
				categoryId:'',//左侧分类id
				treeSelect:[],
				customdate: {
					// name:'',
					url:''
				},
				customNum:1,
				ruleValidate:{
					name: [
						{ required: true, message: '请输入链接名称', trigger: 'blur' }
					],
					url: [
						{ required: true, message: '请输入跳转路径', trigger: 'blur' }
					]
				},
				pid:0
            }
        },
        computed: {
        },
		created(){
        	this.getSort();
			this.goodsCategory();
			let radio = {
				width: 60,
				align: "center",
				render: (h, params) => {
					let id = params.row.id;
					let flag = false;
					if (this.presentId === id) {
						flag = true;
					} else {
						flag = false;
					}
					let self = this;
					return h("div", [
						h("Radio", {
							props: {
								value: flag,
							},
							on: {
								"on-change": () => {
									self.presentId = id;
									this.currenUrl = params.row.url;
								},
							},
						}),
					]);
				},
			};
			this.columns.unshift(radio);
			this.columns7.unshift(radio);
			this.columns8.unshift(radio);
			this.bargain.unshift(radio);
			this.news.unshift(radio);
		},
        methods: {
			handleLoadData (item, callback) {
				item._loading = true;
				pageLink(this.categoryId,item.id).then(res=>{
					item._loading = false;
					res.data.list.forEach((e)=>{
						e.url = `/pages/goods/goods_list/index?sid=${e.id}&title=${e.cate_name}`
					});
					callback(res.data.list);
				});
			},
			// 删除
			delLink (row, tit, num) {
				let delfromData = {
					title: tit,
					num: num,
					url: `diy/del_link/${row.id}`,
					method: 'DELETE',
					ids: ''
				};
				this.$modalSure(delfromData).then((res) => {
					this.$Message.success(res.msg);
					this.tableList.splice(num, 1);
					if(!this.tableList.length){
						this.customNum = 2
					}
				}).catch(res => {
					this.$Message.error(res.msg);
				});
			},
			customLink(){
				this.customNum = 2
			},
			customList(){
				this.customNum = 1
			},
			getCustomList(){
				pageLink(this.categoryId).then(res=>{
					if(!res.data.list.length){
						this.customNum = 2
					}
					this.tableList = res.data.list
				}).catch(err=>{
					this.$Message.error(err.msg)
				})
			},
			handleSubmit (name) {
				this.$refs[name].validate((valid) => {
					if (valid) {
						this.$emit("linkUrl",this.customdate.url);
						this.modals = false
						this.reset();
						// saveLink(this.customdate,this.categoryId).then(res=>{
						// 	this.getCustomList();
						// 	this.$Message.success(res.msg);
						// 	this.$emit("linkUrl",this.customdate.url);
						// 	this.modals = false
						// 	this.reset();
						// }).catch(err=>{
						// 	this.$Message.error(err.msg);
						// })
					} else {
						this.$Message.error('请填写信息');
					}
				})
			},
			handleReset (name) {
				this.$refs[name].resetFields();
			},
			pageChange(index) {
				this.formValidate.page = index;
				this.getList();
			},
			// 商品分类；
			goodsCategory() {
				treeListApi(1)
						.then((res) => {
							this.treeSelect = res.data;
						})
						.catch((res) => {
							this.$Message.error(res.msg);
						});
			},
			// 表格搜索
			userSearchs() {
				this.formValidate.page = 1;
				this.getList();
			},
        	reset(){
				this.currenUrl = "";
				this.presentId = 0;
				this.currenId = "";
				// this.customdate.name="";
				this.customdate.url="";
			},
			getUrl(item){
				this.currenId = item.id;
				this.currenUrl = item.url;
			},
        	getSort(){
				pageCategory().then(res=>{
					res.data[0].children[0].selected = true;
					this.categoryData = res.data;
					this.handleCheckChange('',res.data[0].children[0]);
				}).catch(err=>{
					this.$Message.error(err.msg)
				})
			},
			getList(){
				this.loading = true;
				if(this.currenType == 'product'){
					changeListApi(this.formValidate)
							.then(async (res) => {
								let data = res.data;
								data.list.forEach((e)=>{
									e.url = `/pages/goods_details/index?id=${e.id}`
								});
								this.tableList = data.list;
								this.total = res.data.count;
								this.loading = false;
							})
							.catch((res) => {
								this.loading = false;
								this.$Message.error(res.msg);
							});
				}else if(this.currenType == 'seckill'){
					seckillListApi(this.formValidate)
							.then(async (res) => {
								let data = res.data;
								data.list.forEach((e)=>{
									e.url = `/pages/activity/goods_seckill_details/index?id=${e.id}&status=1`
								});
								this.tableList = data.list;
								this.total = res.data.count;
								this.loading = false;
							})
							.catch((res) => {
								this.loading = false;
								this.$Message.error(res.msg);
							});
				}else if(this.currenType == 'bargain'){
					bargainListApi(this.formValidate)
							.then(async (res) => {
								let data = res.data;
								data.list.forEach((e)=>{
									e.url = `/pages/activity/goods_bargain_details/index?id=${e.id}`
								});
								this.tableList = data.list;
								this.total = res.data.count;
								this.loading = false;
							})
							.catch((res) => {
								this.loading = false;
								this.$Message.error(res.msg);
							});
				}else if(this.currenType == 'combination'){
					combinationListApi(this.formValidate)
							.then(async (res) => {
								let data = res.data;
								data.list.forEach((e)=>{
									e.url = `/pages/activity/goods_combination_details/index?id=${e.id}`
								});
								this.tableList = data.list;
								this.total = res.data.count;
								this.loading = false;
							})
							.catch((res) => {
								this.loading = false;
								this.$Message.error(res.msg);
							});
				}else if(this.currenType == 'news'){
					cmsListApi(this.formValidate).then(async res => {
						let data = res.data
						data.list.forEach((e)=>{
						    e.url=`/pages/extension/news_details/index?id=${e.id}`
						});
						this.tableList = data.list;
						this.total = data.count;
						this.loading = false;
					}).catch(res => {
						this.loading = false;
						this.$Message.error(res.msg);
					})
				}else if(this.currenType == 'integral'){
					integralProductListApi(this.formValidate)
							.then(async (res) => {
								let data = res.data;
								data.list.forEach((e)=>{
									e.url= `/pages/points_mall/integral_goods_details?id=${e.id}`
								});
								this.tableList = data.list;
								this.total = res.data.count;
								this.loading = false;
							})
							.catch((res) => {
								this.loading = false;
								this.$Message.error(res.msg);
							});
				}
			},
			handleCheckChange(data,event){
				this.reset();
        		let id = ''
				if(event.pid){
					id = event.id
					this.categoryId = event.id
				}else {
					return false
				}
				this.loading = true
				this.currenType = event.type
				if(this.currenType == 'product' || this.currenType == 'seckill' || this.currenType == 'bargain' || this.currenType == 'combination' || this.currenType == 'news' || this.currenType == 'integral'){
					this.getList();
				} else if(this.currenType=='custom'){
					this.getCustomList();
				}else {
					pageLink(id,this.pid).then(res=>{
						this.loading = false
						let data = res.data.list;
						if(this.currenType == 'marketing_link'||this.currenType == 'link'){
							let basicsList = [];
							let distributionList = [];
							let userList = [];
							let integral = [];
							let luckDraw = [];
							let coupon = [];
							let presale =[];
							data.forEach((e)=>{
								if(e.type==1){
									basicsList.push(e)
								}else if(e.type==2){
									distributionList.push(e)
								}else if(e.type==3){
									userList.push(e)
								}else if(e.type==4){
									integral.push(e)
								}else if(e.type==5){
									luckDraw.push(e)
								}else if (e.type==6){
                  presale.push(e)
                }else {
									coupon.push(e)
								}
							});
							this.basicsList = basicsList;
							this.distributionList = distributionList;
							this.userList = userList;
							this.coupon = coupon;
							this.luckDraw = luckDraw;
							this.integral = integral;
              this.presale = presale;
						}else if(this.currenType == 'special'){
							let list = []
							data.forEach((e)=>{
								e.url = `/pages/annex/special/index?id=${e.id}`
								if(e.is_diy){
									list.push(e)
								}
							});
							this.total = res.data.count;
							this.tableList = list
						}else if(this.currenType == 'product_category'){
							data.forEach((e)=>{
								if(e.hasOwnProperty('children')){
									e.children.forEach((j)=>{
										j.url = `/pages/goods/goods_list/index?sid=${j.id}&title=${j.cate_name}`
									})
								}
								e.url = `/pages/goods/goods_list/index?cid=${e.id}&title=${e.cate_name}`
							});
							this.tableList = data
						}
					}).catch(err=>{
						this.loading = false
						this.$Message.error(err.msg)
					});
				}
			},
			ok(){
				if(this.currenUrl==""){
					return this.$Message.warning("请选择链接");
				}else {
					this.$emit("linkUrl",this.currenUrl);
					this.modals = false
					this.reset();
				}
			},
			cancel(){
				this.modals = false
				this.reset();
			}
        }
    }
