    import { orderApi } from '@/api/index'
    import echartsFrom from '@/components/echarts/index'
    export default {
        components: { echartsFrom },
        data () {
            return {
                infoList: null,
                visitDate: 'thirtyday',
                series: [],
                yAxisData: []
            }
        },
        methods: {
            // 统计
            getStatistics () {
                let data = {
                    cycle: this.visitDate
                }
                orderApi(data).then(async res => {
                    this.infoList = res.data || {}
                    this.series = this.infoList.series || [],
                        this.yAxisData = [
                            {
                                type: 'value',
                                name: '金额',
                                axisLine: {
                                    show: false
                                },
                                axisTick: {
                                    show: false
                                },
                                axisLabel: {
                                    textStyle: {
                                        color: '#7F8B9C'
                                    }
                                },
                                splitLine: {
                                    show: true,
                                    lineStyle: {
                                        color: '#F5F7F9'
                                    }
                                }
                            },
                            {
                                type: 'value',
                                name: '数量',
                                axisLine: {
                                    show: false
                                },
                                axisTick: {
                                    show: false
                                },
                                axisLabel: {
                                    textStyle: {
                                        color: '#7F8B9C'
                                    }
                                },
                                splitLine: {
                                    show: true,
                                    lineStyle: {
                                        color: '#F5F7F9'
                                    }
                                }
                                // axisLabel: {
                                //     formatter: '{value} °C'
                                // }
                            }
                        ]
                }).catch(res => {
                    this.$Message.error(res.msg)
                })
            },
            // 时间改变
            handleChangeVisitType () {
                this.getStatistics()
            },
            // 监听页面宽度变化，刷新表格
            handleResize () {
                if (this.infoList) this.$refs.visitChart.handleResize()
            }
        },
        created () {
			this.$nextTick(()=>{
				this.getStatistics()
			})
        }
    }
