import { mapState } from 'vuex';
import { storeGetHeaderApi, merchantStoreApi, storeSetShowApi } from '@/api/setting';
import systemStore from '@/components/systemStore/index';
export default {
    name: 'setting_store',
    components: { systemStore },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        ...mapState('admin/userLevel', [
            'categoryId'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 85;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'left';
        }
    },
    data () {
        return {
            grid: {
                xl: 10,
                lg: 10,
                md: 12,
                sm: 24,
                xs: 24
            },
            headeNum: {
                show: { name: '', num: 0 },
                hide: { name: '', num: 0 },
                recycle: { name: '', num: 0 }
            },
            artFrom: {
                page: 1,
                limit: 15,
                type: '0',
                keywords: ''
            },
            loading: false,
            columns: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80,
                    sortable: true
                },
                {
                    title: '提货点图片',
                    slot: 'image',
                    minWidth: 100
                },
                {
                    title: '提货点名称',
                    key: 'name',
                    minWidth: 100
                },
                {
                    title: '提货点电话',
                    key: 'phone',
                    minWidth: 100
                },
                {
                    title: '地址',
                    key: 'detailed_address',
                    minWidth: 100
                },
                {
                    title: '营业时间',
                    key: 'day_time',
                    minWidth: 100
                },
                {
                    title: '是否显示',
                    slot: 'is_show',
                    minWidth: 100
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 120
                }
            ],
            storeLists: [],
            total: 0
        }
    },
    mounted () {
        this.storeHeade();
        this.getList();
    },
    methods: {
        // 获取表单头部信息；
        storeHeade () {
            let that = this;
            storeGetHeaderApi().then(res => {
                that.headeNum = res.data.count;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        getList () {
            let that = this;
            that.loading = true;
            merchantStoreApi(that.artFrom).then(res => {
                that.loading = false;
                that.storeLists = res.data.list;
                that.total = res.data.count;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 搜索；
        userSearchs () {
            this.artFrom.page = 1;
            this.getList();
        },
        // 切换导航；
        onClickTab () {
            this.artFrom.page = 1;
            this.artFrom.keywords = '';
            this.getList();
        },
        pageChange (index) {
            this.artFrom.page = index;
            this.getList();
        },
        // 删除
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `merchant/store/del/${row.id}`,
                method: 'DELETE',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.storeLists.splice(num, 1);
                this.storeHeade();
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 添加提货点；
        add () {
            this.$refs.template.isTemplate = true;
            this.$refs.template.formItem.id = 0;
        },
        onchangeIsShow (id, is_show) {
            let that = this;
            storeSetShowApi(id, is_show).then(res => {
                that.$Message.success(res.msg);
                that.getList();
                that.storeHeade();
            })
        },
        edit (id) {
            this.$refs.template.isTemplate = true;
            this.$refs.template.getInfo(id);
        }
    }
}
