import vueWaterfallEasy from "vue-waterfall-easy";
import { wechatNewsListApi } from "@/api/app";
import { mapState } from "vuex";
export default {
  name: "newsCategory",
  props: {
    scrollerHeight: {
      type: String,
      default: "100%",
    },
    contentTop: {
      type: String,
      default: "205",
    },
    contentWidth: {
      type: String,
      default: "100%",
    },
    maxCols: {
      type: Number,
      default: 8,
    },
    isShow: {
      type: Boolean,
      default: false,
    },
    isShowSend: {
      type: Boolean,
      default: false,
    },
    userIds: {
      type: String,
      default: "",
    },
  },
  components: {
    vueWaterfallEasy,
  },
  data() {
    return {
      isDel: false,
      imgsArr: [],
      group: 0, // 当前加载的加载图片的次数
      fetchImgsArr: [], // 存放每次滚动时下一批要加载的图片的数组
      orderData: {},
      gridPic: {
        xl: 6,
        lg: 8,
        md: 8,
        sm: 24,
        xs: 24,
      },
      grid: {
        xl: 8,
        lg: 8,
        md: 8,
        sm: 24,
        xs: 24,
      },
      formValidate: {
        cate_name: "",
        page: 1,
        limit: 10,
      },
    };
  },
  created() {
    this.getData();
  },
  methods: {
    // 发送图文消息
    send(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `app/wechat/push`,
        method: "post",
        ids: {
          id: row.id,
          user_ids: this.userIds,
        },
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    clickFn(event, { index, value }) {
      event.preventDefault();
      if (event.target.tagName.toLowerCase() === "div") {
        this.$emit("getCentList", value);
      }
    },
    // 删除
    del(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `app/wechat/news/${row.id}`,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          this.$nextTick(() => {
            this.imgsArr = [];
          });
          this.formValidate.page = 1;
          this.getData();
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 删除成功
    // submitModel () {
    //     if (this.delfromData.title === '删除图文') {
    //         // this.imgsArr.splice(this.delfromData.num, 1)
    //         this.$nextTick(() => {
    //             this.imgsArr = [];
    //         })
    //         this.formValidate.page = 1;
    //         this.getData();
    //     }
    // },
    // 编辑
    clkk(item) {
      this.$router.push({
        path: "/admin/app/wechat/news_category/save/" + item.id,
      });
    },
    // 鼠标移进
    mouseenterOut(item) {
      this.$set(item, "isDel", true);
    },
    // 鼠标移出
    mouseenterOver(item) {
      this.$set(item, "isDel", false);
    },
    // 搜索
    userSearchs() {
      this.$nextTick(() => {
        this.imgsArr = [];
      });
      this.formValidate.page = 1;
      this.getData();
    },
    // 瀑布流数据
    getData() {
      wechatNewsListApi(this.formValidate)
        .then(async (res) => {
          if (res.data.list.length === 0) {
            // 模拟已经无新数据，显示 slot="waterfall-over"
            this.imgsArr = [];
            this.$nextTick(() => {
              this.$refs.waterfall.waterfallOver();
            });
          } else {
            let num = Math.ceil(res.data.count / this.formValidate.limit) + 1;
            res.data.list.map((item) => {
              item.isDel = false;
            });
            this.imgsArr = this.imgsArr.concat(res.data.list) || [];
            this.formValidate.page++;
            if (this.formValidate.page === num) {
              // 模拟已经无新数据，显示 slot="waterfall-over"
              this.$refs.waterfall.waterfallOver();
              return;
            }
          }
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
  },
};
