import { orderInvoiceInfo } from '@/api/order'
export default {
    name: "order_detail",
    props:{
        orderId:{
            type:String | Number,
            default:''
        },
    },
    data(){
        return {
            orderDetail:{},
            orderList:[],
            columns1: [
                {
                    title: '商品ID',
                    slot: 'id',
                    maxWidth:80
                },
                {
                    title: '商品名称',
                    slot: 'name',
                    minWidth: 160
                },
                {
                    title: '商品分类',
                    slot: 'className'
                },
                {
                    title: '商品售价',
                    slot: 'price'
                },
                {
                    title: '商品数量',
                    slot: 'total_num'
                }
            ],
            spinShow: false
        }
    },
    mounted() {
        this.getOrderInfo()
    },
    methods:{
        getOrderInfo(){
            this.spinShow = true;
            orderInvoiceInfo(this.orderId).then(res=>{
                this.spinShow = false;
                this.orderDetail = res.data;
                this.orderList = res.data.orderInfo.cartInfo
            }).catch(err=>{
                this.spinShow = false;
                this.$Message.error(err.msg);
                this.$emit('detall',false)
            })
        }
    }
}
