import { mapState } from 'vuex'
export default {
    name: 'picture_cube',
    cname: '图片魔方',
    configName: 'c_picture_cube',
    icon: 'iconcuxiaoliebiao1',
    type: 0, // 0 基础组件 1 营销组件 2工具组件
    defaultName: 'pictureCube', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'pictureCube',
                timestamp: this.num,
                tabConfig: {
                    title: '选择样式',
                    tabVal: 0,
                    type: 1,
                    tabList: [
                        {
                            name: '样式一',
                            icon: 'iconyangshi1',
                            count: 1
                        },
                        {
                            name: '样式二',
                            icon: 'iconyangshi2',
                            count: 2
                        },
                        {
                            name: '样式三',
                            icon: 'iconyangshi3',
                            count: 3
                        },
                        {
                            name: '样式四',
                            icon: 'iconyangshi9',
                            count: 3
                        },
                        {
                            name: '样式五',
                            icon: 'iconyangshi8',
                            count: 4
                        },
                        {
                            name: '样式六',
                            icon: 'iconyangshi4',
                            count: 4
                        }
                    ]
                },
                picStyle: {
                    tabVal: 0,
                    picList: []
                },
                menuConfig: {
                    title: '',
                    maxList: 1,
                    isCube: 1,
                    list: [
                        {
                            img: '',
                            info: [
                                {
                                    title: '链接',
                                    tips: '请输入链接',
                                    value: '',
                                    max: 100
                                }
                            ]

                        }
                    ]
                },
                bgColor: {
                    title: '背景颜色',
                    default: [
                        {
                            item: '#fff'
                        }
                    ],
                    color: [
                        {
                            item: '#fff'
                        }
                    ]
                },
                bgStyle: {
                    title: '背景样式',
                    name: 'bgStyle',
                    type: 0,
                    list: [
                        {
                            val: '直角',
                            icon: 'iconPic_square'
                        },
                        {
                            val: '圆角',
                            icon: 'iconPic_fillet'
                        }
                    ]
                },
                prConfig: {
                    title: '背景边距',
                    val: 0,
                    min: 0
                },
                // 页面间距
                mbConfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                }
            },
            pageData: {},
            style:0,
            picList:[],
            bgColor: [],
            slider: 0,
            bgStyle:0,
            prConfig:0
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.tabConfig){
                this.style = data.tabConfig.tabVal
                this.bgStyle = data.bgStyle.type;
                this.prConfig = data.prConfig.val;
                this.slider = data.mbConfig.val;
                this.bgColor = data.bgColor.color[0].item;
                if(!data.picStyle.picList.length){
                    this.picList = [
                        {
                            "image": "",
                            "link": ""
                        }
                    ]
                }else {
                    this.picList = data.picStyle.picList
                }
            }
        }
    }
}
