import { mapState, mapMutations } from 'vuex';
export default {
    name: 'home_seckill',
    cname: '秒杀',
    configName: 'c_home_seckill',
    icon: 'iconmiaosha1',
    type:1,// 0 基础组件 1 营销组件 2工具组件
    defaultName:'seckill', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },

    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal);
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal];
                this.setConfig(data);
            },
            deep: true
        },
        defaultArray: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num];
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'seckill',
                timestamp: this.num,
                setUp: {
                  tabVal: 0
                },
                countDownColor: {
                    title: '倒计时背景色',
                    name: 'countDownColor',
                    default: [
                        {
                            item: 'rgba(252,60,62,0.09)'
                        }
                    ],
                    color: [
                        {
                            item: 'rgba(252,60,62,0.09)'
                        }
                    ]
                },
                themeColor: {
                    title: '主题风格',
                    name: 'themeColor',
                    default: [
                        {
                            item: '#E93323'
                        }
                    ],
                    color: [
                        {
                            item: '#E93323'
                        }
                    ]
                },
                conStyle: {
                  title: '背景样式',
                  name: 'conStyle',
                  type: 1,
                  list: [
                    {
                      val: '直角',
                      icon: 'iconPic_square'
                    },
                    {
                      val: '圆角',
                      icon: 'iconPic_fillet'
                    }
                  ]
                },
                fontColor:{
                  title: '价格颜色',
                  name: 'fontColor',
                  default: [
                    {
                      item: '#E93323'
                    }
                  ],
                  color: [
                    {
                      item: '#E93323'
                    }
                  ]
                },
                bgColor: {
                  title: '背景颜色',
                  name: 'themeColor',
                  default: [{
                    item: '#fff'
                  }],
                  color: [
                    {
                      item: '#fff'
                    }
                  ]
                },
                prConfig: {
                  title: '背景边距',
                  val: 10,
                  min: 0
                },
                priceShow: {
                  title: '是否显示价格',
                  val: true
                },
                discountShow: {
                  title: '是否显示折扣标签',
                  val: true
                },
                titleShow: {
                  title: '是否显示名称',
                  val: true
                },
                seckillShow: {
                  title: '抢购标签',
                  val: true
                },
                numberConfig: {
                    val: 3
                },
                lrConfig: {
                    title: '左右边距',
                    val: 10,
                    min: 0
                },
                // 页面间距
                mbConfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                },
                imgConfig: {
                    title: '最多可添加1张图片，建议宽度18 * 18px',
                    url: 'http://pro.crmeb.net/static/images/spike-icon-002.gif'
                }
            },
            list: [
                {
                    img: '',
                    name: '小米家用电饭煲小米家用电饭煲',
                    price: '234',
                    discount: '1.2'
                },
                {
                    img: '',
                    name: '小米家用电饭煲小米家用电饭煲',
                    price: '234',
                    discount: '1.2'
                },
                {
                    img: '',
                    name: '小米家用电饭煲小米家用电饭煲',
                    price: '234',
                    discount: '1.2'
                }
            ],
            mTOP: 0,
            listRight: 0,
            countDownColor: '',
            themeColor: '',
            fontColor:'',
            pageData: {},
            imgUrl: '',
            priceShow:true,
            discountShow:true,
            titleShow:true,
            seckillShow:true,
            prConfig:0,
            bgColor: '',
            conStyle:1,
        };
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbConfig){
              this.mTOP = data.mbConfig.val;
              this.listRight = data.lrConfig.val;
              this.countDownColor = data.countDownColor.color[0].item;
              this.themeColor = data.themeColor.color[0].item;
              this.fontColor = data.fontColor.color[0].item;
              this.imgUrl = data.imgConfig.url;
              this.priceShow = data.priceShow.val;
              this.discountShow = data.discountShow.val;
              this.titleShow = data.titleShow.val;
              this.seckillShow = data.seckillShow.val;
              this.prConfig = data.prConfig.val;
              this.bgColor = data.bgColor.color[0].item;
              this.conStyle = data.conStyle.type;
            }
        }
    }
};
