import { mapState } from 'vuex';
import { userListApi } from '@/api/marketing';
import { formatDate } from '@/utils/validate';
export default {
    name: 'storeCouponUser',
    filters: {
        formatDate (time) {
            if (time !== 0) {
                let date = new Date(time * 1000);
                return formatDate(date, 'yyyy-MM-dd hh:mm');
            }
        }
    },
    data () {
        return {
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '优惠券名称',
                    key: 'coupon_title',
                    minWidth: 150
                },
                {
                    title: '领取人',
                    key: 'nickname',
                    minWidth: 130
                },
                {
                    title: '面值',
                    slot: 'coupon_price',
                    minWidth: 100
                },
                {
                    title: '最低消费额',
                    key: 'use_min_price',
                    minWidth: 120
                },
                {
                    title: '开始使用时间',
                    slot: 'start_time',
                    minWidth: 150
                },
                {
                    title: '结束使用时间',
                    slot: 'end_time',
                    minWidth: 150
                },
                {
                    title: '获取方式',
                    key: 'type',
                    minWidth: 150
                },
                {
                    title: '是否可用',
                    slot: 'is_fail',
                    minWidth: 120
                },
                {
                    title: '状态',
                    key: 'status',
                    minWidth: 170
                }
            ],
            tableList: [],
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            tableFrom: {
                status: '',
                coupon_title: '',
                nickname: '',
                page: 1,
                limit: 15
            },
            total: 0
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 96;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    created () {
        this.getList();
    },
    methods: {
        // 列表
        getList () {
            this.loading = true;
            this.tableFrom.status = this.tableFrom.status || '';
            userListApi(this.tableFrom).then(async res => {
                let data = res.data
                this.tableList = data.list;
                this.total = res.data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.tableFrom.page = index;
            this.getList();
        },
        // 搜索()
        orderSearch () {
            this.tableFrom.page = 1;
            this.getList();
        },
        // 表格搜索
        userSearchs () {
            this.tableFrom.page = 1;
            this.getList();
        }
    }
}
