    import { productStoreLabel } from '@/api/product'
    export default {
        name: "storeLabelList",
        props:{
        },
        data(){
            return {
                labelList:[],
								dataLabel:[],
								isStore:false
            }
        },
        mounted() {
        },
        methods:{
					inArray: function(search, array) {
						for (let i in array) {
							if (array[i].id == search) {
								return true;
							}
						}
						return false;
					},
            // 用户标签
            storeLabel(data) {
							this.dataLabel = data;
              productStoreLabel()
                .then((res) => {
									res.data.map(el=>{
										if(el.children){
											this.isStore = true;
											el.children.map(label=>{
												if(this.inArray(label.id, this.dataLabel)){
													label.disabled = true;
												}else{
													label.disabled = false;
												}
											})
										}  
									})
									this.labelList =res.data
                })
                .catch((res) => {
                  this.$Message.error(res.msg);
                });
            },
            selectLabel(label){
                if(label.disabled){
										let index = this.dataLabel.indexOf(this.dataLabel.filter(d=>d.id == label.id)[0]);
										this.dataLabel.splice(index,1);
                    label.disabled = false
                }else{
										this.dataLabel.push({'label_name':label.label_name,'id':label.id});
                    label.disabled = true
                }
            },
            // 确定
            subBtn(){
							this.$emit('activeData',JSON.parse(JSON.stringify(this.dataLabel)))
            },
            cancel(){
              this.$emit('close')
            }
        }
    }
